    "VARIABLES": "Let x be the number of mango bubble teas made and y be the number of lychee bubble teas made, both integers.",
    "CONSTRAINTS": {
        "Mango Juice": "4x \leq 2000",
        "Lychee Juice": "6y \leq 3000",
        "Tea Constraint": "8x + 6y \leq T",
        "Minimum Lychee Percentage": "y \geq 0.4(x+y)",
        "Mango Tea Sales": "x \geq y",
        "Non-negativity": "x, y \geq 0, x, y \in Z"
    },
    "OBJECTIVE": "Minimize T (total amount of tea needed): Minimize T = 8x + 6y"
}
