def maximize_fish_catch(net, fishing_line, sum_in_a, linear_available, linear_at_most):
    max_fish = 0

    for i in range(net+1):
        for j in range(fishing_line+1):
            if (i*8 + j*5) > max_fish and (i*4 + j*3) <= linear_available and (i*2 + j) <= linear_at_most:
                max_fish = i*8 + j*5

    return max_fish

