    "VARIABLES": "Let x be the number of cars and y be the number of bikes.",
    "CONSTRAINTS": {
        "Constraint 1": "x + y \geq 100 (to transport at least 500 people)",
        "Constraint 2": "5x + 3y \geq 500 (each car can take 5 people and each bike can take 3 people)",
        "Constraint 3": "x \leq 0.4(x + y) (at most 40% of vehicles can be cars)"
    },
    "OBJECTIVE": "Minimize y (total number of bikes needed)"
}
